package com.rks.catalog.controllers;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.data.redis.core.StringRedisTemplate;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestController@RequestMapping("/api/v1")public class ProductLikeController {    private final Logger logger = LoggerFactory.getLogger(ProductLikeController.class);    @Autowired    StringRedisTemplate template;    @GetMapping("/products/sku/like")    public void likeProduct(String sku) {        logger.info("in:ProductLikeController:likeProduct");        template.opsForZSet().incrementScore("product-likes", sku, 1);        template.opsForZSet().rank("product-likes", "aa-kk-vol-2");    }}