package com.rks.catalog.controllers;import org.bson.Document;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.data.mongodb.core.MongoTemplate;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestController@RequestMapping("/api/v1")public class TestController {    @Autowired    private MongoTemplate mongoTemplate;    @PostMapping("/addjson")    ResponseEntity<?> addJson(@RequestBody String jsonString) {        Document document = Document.parse(jsonString);        mongoTemplate.insert(document, "products");        return new ResponseEntity(null, HttpStatus.CREATED);    }}