package com.rks.catalog.domain.category;import com.fasterxml.jackson.annotation.JsonInclude;import lombok.Builder;import lombok.Data;import lombok.ToString;import org.springframework.data.mongodb.core.mapping.Document;import java.io.Serializable;import java.util.Map;@JsonInclude(JsonInclude.Include.NON_NULL)@Builder@Data@Document(collection = "categories")@ToStringpublic class Category implements Serializable {    private String id;    private String name;    private String description;}