package com.rks.catalog.domain.product;import com.fasterxml.jackson.annotation.JsonInclude;import com.rks.catalog.domain.BaseEntity;import lombok.Builder;import lombok.Data;import org.springframework.data.mongodb.core.mapping.Document;import java.io.Serializable;@JsonInclude(JsonInclude.Include.NON_NULL)@Builder@Data@Document(collection = "products")public class Product extends BaseEntity {    private String id;    private String type;    private String sku;    private String name;    private String category;    private String description;    private ShippingInfo shipping;    private PriceInfo pricing;    private DetailInfo details;    private String imageUrl;    private Object productDetails;}