package com.rks.catalog.entity.category;import com.fasterxml.jackson.annotation.JsonInclude;import lombok.Data;import lombok.NoArgsConstructor;import lombok.ToString;import org.springframework.data.mongodb.core.mapping.Document;import java.io.Serializable;@NoArgsConstructor@JsonInclude(JsonInclude.Include.NON_NULL)@Data@Document(collection = "categories")@ToStringpublic class Category implements Serializable {    private String id;    private String name;    private String description;}