package com.rks.catalog.redis;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.data.redis.connection.RedisConnectionFactory;import org.springframework.data.redis.connection.RedisStandaloneConfiguration;import org.springframework.data.redis.connection.lettuce.LettuceConnectionFactory;import org.springframework.data.redis.core.RedisTemplate;import org.springframework.data.redis.core.StringRedisTemplate;@Configurationpublic class RedisConfig {    @Bean    RedisConnectionFactory redisConnectionFactory() {        LettuceConnectionFactory factory = new LettuceConnectionFactory(                new RedisStandaloneConfiguration("localhost", 6379));        return factory;    }    @Bean    RedisTemplate<String, String> redisTemplate() {        RedisTemplate<String, String> template = new RedisTemplate<>();        template.setConnectionFactory(redisConnectionFactory());        return template;    }    @Bean    StringRedisTemplate stringRedisTemplate() {        return new StringRedisTemplate(redisConnectionFactory());    }}