package com.rks.catalog.repositories;import com.rks.catalog.entity.product.Product;import org.springframework.data.mongodb.repository.MongoRepository;import org.springframework.data.mongodb.repository.Query;import java.util.List;public interface ProductRepository extends MongoRepository<Product, String> {    List<Product> findByCategory(String category);    @Query("{ 'categories' : {'$regex': ?0, '$options': 'i'} }")    List<Product> findProductsByCategoryName(String categoryName);    Product findBySku(String sku);}