package com.rks.catalog.service.impl;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.data.redis.core.StringRedisTemplate;import org.springframework.stereotype.Service;import java.util.UUID;import java.util.concurrent.TimeUnit;@Servicepublic class RedisCounterServiceImpl {    public static final Logger logger = LoggerFactory.getLogger(RedisCounterServiceImpl.class);    @Autowired    StringRedisTemplate stringRedisTemplate;    String key = UUID.randomUUID().toString().substring(1,8);    public void increaseCounterInRedis(String key) {        boolean keyNotFoundAndSet = stringRedisTemplate.boundValueOps(key).setIfAbsent(String.valueOf(1), 1, TimeUnit.HOURS);        if (!keyNotFoundAndSet) {            stringRedisTemplate.boundValueOps(key).increment();        }    }}